import { useContext, useEffect, useState } from "react";
import MagnifierIcon from "./MagnifierIcon";
import classes from "./SearchBar.module.css";
import MovieCard from "./MovieCard";
import MoviesContext from "../store/movies-context";
const API_URL = "http://www.omdbapi.com/?apikey=f51ddc1a";

const SearchBar = (props) => {
  const moviesCtx = useContext(MoviesContext);
  const [query, setQuery] = useState("");

  const inputChangeHandler = (event) => {
    setQuery(event.target.value);
  };

  useEffect(() => {
    if (query.length > 2) {
      console.log("IDEEE");
      const timer = setTimeout(() => moviesCtx.findSearchedMovies(query), 2000);
      return () => clearTimeout(timer);
    }
  }, [query]);

  //console.log(shows);
  return (
    <>
      <div className={classes["search-box"]}>
        <span className={classes["search-box__icon"]}>
          <MagnifierIcon />
        </span>
        <input
          type="text"
          value={query}
          className={classes["search-box__input"]}
          placeholder="Search"
          onChange={inputChangeHandler}
        />
      </div>
      {query.length > 2 && (
        <ul className={classes["card-container"]}>
          {moviesCtx.searchedMovies.map((show) => (
            <MovieCard key={show.imdbID} movieObject={show} />
          ))}
        </ul>
      )}
    </>
  );
};

export default SearchBar;
