import { useEffect, useState } from "react";
import MagnifierIcon from "./MagnifierIcon";
import classes from "./SearchBar.module.css";

const SearchBar = () => {
  const [query, setQuery] = useState("");
  const inputChangeHandler = (event) => {
    setQuery(event.target.value);
  };

  useEffect(() => {
    if (query.length > 2) {
      console.log("IDEE"); //ovdje ce ici fja koja ce slati request u bazu podataka, a tu bi mogli koristit react context
    }
  }, [query, setQuery]);

  return (
    <div className={classes["search-box"]}>
      <span className={classes["search-box__icon"]}>
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
          <path d="M22.707 21.293l-6.184-6.184a8.5 8.5 0 1 0-1.414 1.414l6.184 6.184a1 1 0 0 0 1.414 0 1 1 0 0 0 0-1.414zM3.5 10.5a7 7 0 1 1 14 0 7 7 0 0 1-14 0z" />
        </svg>
      </span>
      <input
        type="text"
        value={query}
        className={classes["search-box__input"]}
        placeholder="Search"
        onChange={inputChangeHandler}
      />
    </div>
  );
};

export default SearchBar;
