import { useContext, useEffect, useState } from "react";
import MagnifierIcon from "../ui/MagnifierIcon";
import classes from "./SearchBar.module.css";
import MovieCard from "./MovieCard";
import MoviesContext from "../store/movies-context";
import ShowsContext from "../store/shows-context";

const SearchBar = (props) => {
  const moviesCtx = useContext(MoviesContext);
  const showsCtx = useContext(ShowsContext);
  const [query, setQuery] = useState("");

  useEffect(() => {
    const storedQuery = sessionStorage.getItem("searchQuery");
    if (storedQuery) {
      setQuery(storedQuery);
    }
  }, []);

  const inputChangeHandler = (event) => {
    setQuery(event.target.value);
  };

  useEffect(() => {
    sessionStorage.setItem("searchQuery", query);
    if (query.length > 2) {
      let timer;
      if (props.type === "movie") {
        timer = setTimeout(() => moviesCtx.findSearchedMovies(query), 2000);  
      } else if (props.type === "series") {
        timer = setTimeout(() => showsCtx.findSearchedShows(query), 2000);
      }
      return () => clearTimeout(timer);
    }
  }, [query]);

  //console.log(shows);
  return (
    <>
      <div className={classes["search-box"]}>
        <span className={classes["search-box__icon"]}>
          <MagnifierIcon />
        </span>
        <input
          type="text"
          value={query}
          className={classes["search-box__input"]}
          placeholder="Search"
          onChange={inputChangeHandler}
        />
      </div>
      {query.length > 2 && (
        <ul className={classes["card-container"]}>
          {props.type === 'movie' && moviesCtx.searchedMovies.map((show) => (
            <MovieCard key={show.imdbID} movieObject={show} />
          ))}
          {props.type === 'series' && showsCtx.searchedShows.map((show) => (
            <MovieCard key={show.imdbID} movieObject={show} />
          )) }
        </ul>
      )}
    </>
  );
};

export default SearchBar;
