import { useEffect, useState } from "react";

const SearchBar = () => {
  const [query, setQuery] = useState("");
  const inputChangeHandler = (event) => {
    setQuery(event.target.value);
  };

  useEffect(() => {
    if (query.length > 2) {
      console.log('IDEE'); //ovdje ce ici fja koja ce slati request u bazu podataka, a tu bi mogli koristit react context
    }
  }, [query, setQuery]);

  return (
    <input type="text" value={query} onChange={inputChangeHandler}></input>
  );
};

export default SearchBar;
