import { useParams } from "react-router-dom";
import { useContext, useState } from "react";
import MoviesContext from "../store/movies-context";
import ShowsContext from "../store/shows-context";
import classes from "./PlotDetails.module.css";
const API_URL = "http://www.omdbapi.com/?apikey=f51ddc1a";

const PlotDetails = () => {
  const params = useParams();
  /*const moviesCtx = useContext(MoviesContext);
  const showsCtx = useContext(ShowsContext);

  //console.log(moviesCtx.top10Movies);
  console.log(moviesCtx.searchedMovies);
  /*console.log(moviesCtx.top10Movies);
  console.log(moviesCtx.top10Movies);

  const allShows = [
    ...moviesCtx.top10Movies,
    ...moviesCtx.searchedMovies,
    ...showsCtx.top10Shows,
    ...showsCtx.searchedShows,
  ];
console.log(allShows);
console.log (params.imdbID);
  const chosenShow = allShows.find(
    (showObject) => showObject.imdbID === params.imdbID
  );*/
  //let [chosenShowPoster, chosenShowTitle, chosenShowPlot] = ['','',''];
  const [chosenShowPoster, setChosenShowPoster] = useState("");
  const [chosenShowTitle, setChosenShowTitle] = useState("");
  const [chosenShowPlot, setChosenShowPlot] = useState("");

  fetch(`${API_URL}&i=${params.imdbID}&plot=full`)
    .then((response) => {
      if (!response.ok) {
        throw new Error("NE VALJA BRUDA");
      } else {
        return response.json();
      }
    })
    .then((data) => {
      console.log(data);
      if (data) {
        setChosenShowPoster(data.Poster);
        setChosenShowTitle(data.Title);
        setChosenShowPlot(data.Plot);
      }
    })
    .catch((error) => {
      console.error(error);
    });

  return (
    <div className={classes.image}>
      <img src={chosenShowPoster} alt={chosenShowTitle} />
      <h3>{chosenShowTitle}</h3>
      <p>
        Movie overview: <br /> {chosenShowPlot}
      </p>
    </div>
  );
};

export default PlotDetails;
