import ShowsContext from ".shows-context";
import { useReducer } from "react";

const defaultShowsState = {
  shows: [],
};

const showsReducer = (state, action) => {
  if (action.type === "SHOWTOP10") {
  }
  if (action.type === "SHOWSEARCHED") {
  }
};

const ShowsProvider = (props) => {
  const [showsState, dispatchShowsAction] = useReducer(
    showsReducer,
    defaultShowsState
  );

  const showTop10ShowsHandler = () => {
    dispatchShowsAction({ type: "SHOWTOP10" });
  };

  const showSearchedShowsHandler = (query) => {
    dispatchShowsAction({ type: "SHOWSEARCHED" });
  };

  const showsContext = {
    shows: showsState,
    top10Shows: showTop10ShowsHandler,
    searchedShows: showSearchedShowsHandler,
  };

  return (
    <ShowsContext.Provider value={showsContext}>
      {props.children}
    </ShowsContext.Provider>
  );
};

export default ShowsProvider;
