import classes from "./NavBar.module.css";
import { NavLink } from "react-router-dom";

const NavBar = () => {
  window.addEventListener("click", () => localStorage.clear());
  return (
    <nav className={classes.nav}>
      <ul>
        <NavLink
          to="/"
          className={({ isActive }) => (isActive ? classes.active : undefined)}
          end
        >
          Movies
        </NavLink>
        <NavLink
          to="/tvshows"
          className={({ isActive }) => (isActive ? classes.active : undefined)}
          end /*NavLink kao classname prima fju koja vraca ime klase koje se dodaje anchor tagu*/
        >
          TV Shows
        </NavLink>
      </ul>
    </nav>
  );
};
export default NavBar;
