import React from "react";
import { createBrowserRouter, RouterProvider } from "react-router-dom";
import RootLayout from "./pages/Root";
import MoviesPage, { loader as top10MoviesLoader } from "./pages/Movies";
import TVShowsPage, { loader as top10TVShowsLoader } from "./pages/TVShows";
import ShowsDetailsPage from "./pages/ShowsDetails";
import MoviesRootPage from "./pages/MoviesRoot";
import TVShowsRootPage from "./pages/TVShowsRoot";
import MoviesProvider from "./store/MoviesProvider";
import ShowsProvider from "./store/ShowsProvider";

const router = createBrowserRouter([
  {
    path: "/",
    element: <RootLayout />,
    children: [
      {
        path: "",
        element: <MoviesRootPage />,
        children: [
          {
            index: true,
            element: <MoviesPage />,
          },
          {
            path: ":imdbID",
            element: <ShowsDetailsPage />,
          },
        ] /*loader: top10MoviesLoader*/,
      },
      {
        path: "tvshows",
        element: <TVShowsRootPage /> /*loader: top10TVShowsLoader*/,
        children: [
          {
            index: true,
            element: <TVShowsPage />,
          },
          {
            path: ":imdbID",
            element: <ShowsDetailsPage />,
          },
        ],
      },
    ],
  },
]);

const App = () => {
  return (
    <MoviesProvider>
      <ShowsProvider>
        <RouterProvider router={router} />
      </ShowsProvider>
    </MoviesProvider>
  );
};

export default App;
