import { useEffect, useState } from "react";
import MagnifierIcon from "./MagnifierIcon";
import classes from "./SearchBar.module.css";
import MovieCard from "./MovieCard";
const API_URL = "http://www.omdbapi.com/?apikey=f51ddc1a";

const SearchBar = (props) => {
  const [query, setQuery] = useState("");
  const [shows, setShows] = useState([]);
  let timer;

  const searchQuery = async (title) => {
    console.log(`${API_URL}&type=${props.type}&s=${title}`);
    const response = await fetch(`${API_URL}&type=${props.type}&s=${title}`);
    if (!response.ok) {
      throw new Error("NE VALJA BRUDA");
    } else {
      const data = await response.json();
      //console.log(data.Search);
      if (data.Search) {
        setShows(data.Search);
      } else {
        setShows([]);
      }
    }
  };

  const inputChangeHandler = (event) => {
    setQuery(event.target.value);
  };

  useEffect(() => {
    if (query.length > 2) {
      clearTimeout(timer);
      console.log("IDEEE");
      timer = setTimeout(searchQuery(query), 200);
    }
  }, [query]);

  console.log(shows);
  return (
    <>
      <div className={classes["search-box"]}>
        <span className={classes["search-box__icon"]}>
          <MagnifierIcon />
        </span>
        <input
          type="text"
          value={query}
          className={classes["search-box__input"]}
          placeholder="Search"
          onChange={inputChangeHandler}
        />
      </div>
      {query.length > 2 && (
        <ul className={classes["card-container"]}>
          {shows.map((show) => <MovieCard key={show.imdbID} movieObject={show} />)}
        </ul>
      )}
    </>
  );
};

export default SearchBar;
