import { useReducer } from "react";
import MoviesContext from "./movies-context";
const API_URL = "http://www.omdbapi.com/?apikey=f51ddc1a";

const defaultMoviesState = {
  top10Movies: [],
  searchedMovies: [],
};

const moviesReducer = (state, action) => {
  if (action.type === "SHOWTOP10") {
  }
  if (action.type === "SHOWSEARCHED") {
    console.log("UDJEIOVDJEBGMI")
    fetch(`${API_URL}&type=movie&s=${action.query}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error("NE VALJA BRUDA");
        } else {
          return response.json();
        }
      })
      .then((data) => {
        console.log(data.Search);
        if (data.Search) {
          const updatedState = { ...state, searchedMovies: data.Search };
          console.log(updatedState);
          return updatedState;
        } else {
          const updatedState = { ...state, searchedMovies: [] };
          return updatedState;
        }
      })
      .catch((error) => {
        console.error(error);
      });
  }

  /*{
    const response = await fetch(`${API_URL}&type=${props.type}&s=${title}`);
    // const response = await fetch(
    //   `${API_URL}&type=${props.type}&s=&r=json&v=1page=1sort=imdbRating&order=desc`
    // );
    if (!response.ok) {
      throw new Error("NE VALJA BRUDA");
    } else {
      const data = await response.json();
    }
    if (data.Search) {
      const updatedState = {...data.Search}
    } else {
      state.searchedMovies=[];
    }
  }*/
  return state;
};

const MoviesProvider = (props) => {
  const [moviesState, dispatchMoviesAction] = useReducer(
    moviesReducer,
    defaultMoviesState
  );

  const findTop10MoviesHandler = () => {
    console.log()
    dispatchMoviesAction({ type: "SHOWTOP10" });
  };

  const findSearchedMoviesHandler = (query) => {
    console.log("NADJEEME")
    dispatchMoviesAction({ type: "SHOWSEARCHED", query});
  };

  const moviesContext = {
    top10Movies: moviesState.top10Movies,
    searchedMovies: moviesState.searchedMovies,
    findTop10Movies: findTop10MoviesHandler,
    findSearchedMovies: findSearchedMoviesHandler,
  };

  return (
    <MoviesContext.Provider value={moviesContext}>
      {props.children}
    </MoviesContext.Provider>
  );
};
export default MoviesProvider;
